import GET_COUNTRIES from '../actions/index'

const initialState = {
  countries: []
}

export default function(state = initialState, action) {
  switch(action.type) {
    case GET_COUNTRIES:
      return {
        ...state,
        countries: getCountriesInfo(action.payload)
      }
      default:
        return state
  }
}

function getCountriesInfo(data){
  return data.map( c => {
    return {
      name: c.name,
      currencyCode: c.currency[0].code,
      flag: c.flag,
      code: c.alpha3Code
    }
  })
}
