import axios from 'axios'

export const GET_COUNTRIES = "GET_COUNTRIES"
export const GET_RATE_EXCHANGE = "GET_RATE_EXCHANGE"

export function fetchCountries() {
  return function (dispatch) {
    axios.get('https://restcountries.eu/rest/v2/all').then(res => {
    dispatch({
      type:GET_COUNTRIES,
      payload:res.data
    })
  })
  }
}

export function fetchRateExchange (country) {
  return function (dispatch) {
    axios.get(`https://api.exchangeratesapi.io/history?start_at=2019-01-01&end_at=${new Date('Y-m-d')}&base=USD&symbols=${country.code}`).then(res => {
      dispatch({
        type: GET_RATE_EXCHANGE,
        payload: {
          rates: res.data.rates,
          ...country
        }
      })
    })
  }
}
