import axios from 'axios'

export const GET_COUNTRIES = "GET_COUNTRIES"

export default function fetchCountries() {
  return function (dispatch) {
    axios.get('https://restcountries.eu/rest/v2/all').then(res => {
    dispatch({
      type:GET_COUNTRIES,
      payload:res.data
    })
  })
  }
}
